import React, { useState } from 'react';
import Card from './Card';
import { PropTypes } from 'prop-types';

const utils = {
    range: (min, max) => Array.from({length: max - min + 1}, (_, i) => min + i),

    random: (min, max) => min + Math.floor(Math.random() * (max - min + 1)),
}

const createRandomNumbers = () => {
    let array1 = utils.range(1, 6);
    let array2 = utils.range(1, 6);

    return array1.concat(array2);
}

const CardList = (props) => {
    const [availableNumbers, setAvailableNumbers] = useState(createRandomNumbers());
    const [usedNumbers, setUsedNumbers] = useState([]);

    return (
        <div className="game-field">             
            <div className="row">                
                <Card number={availableNumbers[0]} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide} />                                
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
            </div>
            <div className="row">                
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>                                
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
            </div>
            <div className="row">                
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>                                
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
            </div>
        </div>
        );
}

CardList.propTypes  = {    
    addToSelecteds: PropTypes.func,
    removeFromSelecteds: PropTypes.func,
    canHide: PropTypes.func
}

export default CardList;
