import React, { useState } from 'react';
import './GameField.css'
import Card from './Card';

function GameField() {
    const [selecteds, setSelecteds] = useState([]);
    const [availables, setAvailables] = useState([1, 2, 3, 4, 5]);  
    
    var canReveal = (number) => {
        if (selecteds.length === 2)
            return false;
        return true;
    }

    const addToSelecteds = (number) => {
        var canReveal = canReveal(number);
        if (canReveal){        
            setSelecteds(selecteds.concat(number));

            if (selecteds.length === 2 && selecteds[0] === selecteds[1]){
                setAvailables(availables.filter(e => e === number ));
                setSelecteds([]);
            }           
        }
        return canReveal;
    }    

    return (
        <div className="game-field">             
            <div className="row">                
                <Card number={1} onReveal={addToSelecteds} />                                
                <Card number={4} />
                <Card number={2} />
                <Card number={1} />
            </div>
            <div className="row">                
                <Card number={5} />                                
                <Card number={2} />
                <Card number={3} />
                <Card number={6} />
            </div>
            <div className="row">                
                <Card number={4} />                                
                <Card number={6} />
                <Card number={5} />
                <Card number={3} />
            </div>
        </div>
        );
}

export default GameField;
