import React, { useState } from 'react';
import './GameField.css'
import Card from './Card';

function GameField() {
    const [selecteds, setSelecteds] = useState([]);
    const [availables, setAvailables] = useState([1, 2, 3, 4, 5]);  
    

    const addToSelecteds = (number) => {
        let canReveal = (selecteds.length === 2) ? false : true;
        if (canReveal){        
            console.log("Adding", number);
            let newSelecteds = selecteds.concat(number);
            setSelecteds(newSelecteds);
            console.log("selected", selecteds);
            if (selecteds.length === 2 && selecteds[0] === selecteds[1]){ 
               
                setAvailables(availables.filter(e => e === number ));
                setSelecteds([]);
            }           
        }
        return canReveal;
    }   

    return (
        <div className="game-field">             
            <div className="row">                
                <Card number={1} onReveal={addToSelecteds} />                                
                <Card number={4} />
                <Card number={2} />
                <Card number={1} onReveal={addToSelecteds}/>
            </div>
            <div className="row">                
                <Card number={5} />                                
                <Card number={2} />
                <Card number={3} />
                <Card number={6} />
            </div>
            <div className="row">                
                <Card number={4} />                                
                <Card number={6} />
                <Card number={5} />
                <Card number={3} />
            </div>
        </div>
        );
}

export default GameField;
