import { useState, useEffect } from 'react';

const useCardsHooks = () => {
    const [showImg, setShowImg] = useState(false);    
    const [useGrayImg, setUseGrayImg] = useState(false);

    const useCardGrayImageEffect = (number, availablesNumbers) => {
        useEffect(() => {                      
            if (typeof number !== 'undefined' 
                    && typeof availables !== 'undefined' 
                    && !availablesNumbers.includes(number)){
                setUseGrayImg(true);
            }
            return () => { }            
        },[availablesNumbers])
    }

    return {showImg, setShowImg, useGrayImg  }
}

export default useCardsHooks;
