import React, { useState } from 'react';
import Card from './Card';
import { PropTypes } from 'prop-types';

const utils = {
    random: (min, max) => min + Math.floor(Math.random() * (max - min + 1)),
}

const CardList = (props) => {
    const [availableNumbers, setAvailableNumbers] = useState([1, 2, 3, 4, 5, 6]);
    const [usedNumbers, setUsedNumbers] = useState([]);

    const getRandomNumbers = () => {
        let number = utils.random(1, 6);
        if (usedNumbers.includes(number))
        setAvailableNumbers(availableNumbers.filter(n => n !== number));
        else 
            usedNumbers.concat(number);   
        return number;
    }

    return (
        <div className="game-field">             
            <div className="row">                
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide} />                                
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
            </div>
            <div className="row">                
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>                                
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
            </div>
            <div className="row">                
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>                                
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers()} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
            </div>
        </div>
        );
}

CardList.propTypes  = {    
    addToSelecteds: PropTypes.func,
    removeFromSelecteds: PropTypes.func,
    canHide: PropTypes.func
}

export default CardList;
