import React from 'react';
import { PropTypes } from 'prop-types';
import './Card.css'
import CardBack from '../images/back_card.jpg';
import useCardsHooks from '../CustomHooks/UseCardsHooks';
import { useEffect } from 'react';

function Card(props) {    
    const {
        showImg,
        setShowImg,
        useGrayImg,        
    } = useCardsHooks(props.number, props.availables);

    
    useEffect(() => {    
        console.log("Using Effect");                  
        if (typeof number !== 'undefined' 
                && typeof availables !== 'undefined' 
                && !props.availablesNumbers.includes(props.number)){
            setUseGrayImg(true);
        }
        return () => { }            
    },[props.availablesNumbers])  

    const revealNumber = () => {              
        setShowImg(props.onReveal(props.number));             
    }

    const hideNumber = () => {
        if (props.canHide(props.number))
        {
            props.onHide(props.number);
            setShowImg(false);
        }        
    }

    return (
        <div className="col-sm-3">           
            <input 
                type="image" 
                onClick={revealNumber} 
                className="img-card" 
                src={CardBack}
                style={{display: showImg ? 'none' : 'inline'}}
                >
            </input>       
            <input 
                type="image"
                onClick={hideNumber}          
                className="img-card"
                src={!useGrayImg ? props.buildImageUrl(props.number): props.buildGrayImageUrl(props.number)}
                style={{display: showImg ? 'inline' : 'none'}}     
                >
            </input>
        </div>
        );
}

Card.propTypes  = {
    buildImageUrl: PropTypes.func,
    buildGrayImageUrl: PropTypes.func,
    number: PropTypes.number,    
    onReveal: PropTypes.func,
    onHide: PropTypes.func,
    canHide: PropTypes.func,
    availables: PropTypes.array
}

export default Card;
