import React,  { useState } from 'react';
import { PropTypes } from 'prop-types';
import './Card.css'
import CardBack from '../images/back_card.jpg';

function Card(props) {    
    const [showImg, setShowImg] = useState(true);

    const revealNumber = () => {
        if (props.checkStatus(props.number) === 'selected')
            return;

        setShowImg(false);
    }

    const hideNumber = () => {
        if (props.checkStatus(props.number) === 'used')
            return;

        setShowImg(true);
    }

    const buildSrc = (number) => {
        return "/images/" + number + ".jpg";
    }

    return (
        <div className="col-sm-3">           
            <input 
                type="image" 
                onClick={revealNumber} 
                className="img-card" 
                src={CardBack}
                style={{display: showImg ? 'inline' : 'none'}}
                >
            </input>       
            <input 
                type="image"
                onClick={hideNumber}          
                className="img-card"
                src={buildSrc(props.number)}
                style={{display: !showImg ? 'inline' : 'none'}}     
                >
            </input>
        </div>
        );
}

Card.propTypes  = {
    number: PropTypes.number,
    checkStatus: PropTypes.Func
}

export default Card;
