import React,  { useState } from 'react';
import { PropTypes } from 'prop-types';
import './Card.css'
import CardBack from '../images/back_card.jpg';

function Card(props) {    
    const [showImg, setShowImg] = useState(false);
    const [number, setNumber] = useState(props.setNumber());

    const revealNumber = () => {             
        setShowImg(props.onReveal(props.number));
    }

    const hideNumber = () => {
        if (props.canHide(props.number))
        {
            props.onHide(props.number);
            setShowImg(false);
        }        
    }

    const buildSrc = (number) => {
        return "/images/" + number + ".jpg";
    }

    return (
        <div className="col-sm-3">           
            <input 
                type="image" 
                onClick={revealNumber} 
                className="img-card" 
                src={CardBack}
                style={{display: showImg ? 'none' : 'inline'}}
                >
            </input>       
            <input 
                type="image"
                onClick={hideNumber}          
                className="img-card"
                src={buildSrc(props.number)}
                style={{display: showImg ? 'inline' : 'none'}}     
                >
            </input>
        </div>
        );
}

Card.propTypes  = {
    number: PropTypes.number,
    onReveal: PropTypes.func,
    onHide: PropTypes.func,
    canHide: PropTypes.func
}

export default Card;
