import React from 'react';
import Card from './Card';
import { PropTypes } from 'prop-types';

const utils = {
    range: (min, max) => Array.from({length: max - min + 1}, (_, i) => min + i),

    random: (min, max) => min + Math.floor(Math.random() * (max - min + 1)),
}

function shuffle(array) {
    let counter = array.length;

    // While there are elements in the array
    while (counter > 0) {
        // Pick a random index
        let index = Math.floor(Math.random() * counter);

        // Decrease counter by 1
        counter--;

        // And swap the last element with it
        let temp = array[counter];
        array[counter] = array[index];
        array[index] = temp;
    }

    return array;
}

const createRandomNumbers = () => {
    let cardsNumbers = utils.range(1, 6).concat(utils.range(1, 6));   
    let shuffleCardNumbers =  shuffle(cardsNumbers);
    console.log(shuffleCardNumbers);
    return  shuffleCardNumbers;
}

class CardList extends React.Component {
    constructor(props){
        super(props);
        this.onReveal = props.addToSelecteds;
        this.onHide = props.removeFromSelecteds;
        this.canHide = props.canHide;
        this.cardNumbers = createRandomNumbers();
    }

    render() { return (

        <div className="game-field">             
            <div className="row">                
                <Card number={this.cardNumbers[0]} onReveal={this.addToSelecteds} onHide={this.removeFromSelecteds} canHide={this.canHide} />                                
                <Card number={this.cardNumbers[1]} onReveal={this.addToSelecteds} onHide={this.removeFromSelecteds} canHide={this.canHide}/>
                <Card number={this.cardNumbers[2]} onReveal={this.addToSelecteds} onHide={this.removeFromSelecteds} canHide={this.canHide}/>
                <Card number={this.cardNumbers[3]} onReveal={this.addToSelecteds} onHide={this.removeFromSelecteds} canHide={this.canHide}/>
            </div>
            <div className="row">                
                <Card number={this.cardNumbers[4]} onReveal={this.addToSelecteds} onHide={this.removeFromSelecteds} canHide={this.canHide}/>                                
                <Card number={this.cardNumbers[5]} onReveal={this.addToSelecteds} onHide={this.removeFromSelecteds} canHide={this.canHide}/>
                <Card number={this.cardNumbers[6]} onReveal={this.addToSelecteds} onHide={this.removeFromSelecteds} canHide={this.canHide}/>
                <Card number={this.cardNumbers[7]} onReveal={this.addToSelecteds} onHide={this.removeFromSelecteds} canHide={this.canHide}/>
            </div>
            <div className="row">                
                <Card number={this.cardNumbers[8]} onReveal={this.addToSelecteds} onHide={this.removeFromSelecteds} canHide={this.canHide}/>                                
                <Card number={this.cardNumbers[9]} onReveal={this.addToSelecteds} onHide={this.removeFromSelecteds} canHide={this.canHide}/>
                <Card number={this.cardNumbers[10]} onReveal={this.addToSelecteds} onHide={this.removeFromSelecteds} canHide={this.canHide}/>
                <Card number={this.cardNumbers[11]} onReveal={this.addToSelecteds} onHide={this.removeFromSelecteds} canHide={this.canHide}/>
            </div>
        </div>
    )};
}

CardList.propTypes  = {    
    addToSelecteds: PropTypes.func,
    removeFromSelecteds: PropTypes.func,
    canHide: PropTypes.func
}

export default CardList;
