import React, { useState } from 'react';
import './GameField.css'
import Card from './Card';

function GameField() {
    const [selecteds, setSelecteds] = useState([]);
    const [availables, setAvailables] = useState([1, 2, 3, 4, 5]);    

    const setStatus = (number) => {
        if (availables.includes(number))
            return 'available';
        return 'used';
    }

    return (
        <div className="game-field">             
            <div className="row">                
                <Card number={1} checkStatus={setStatus}/>                                
                <Card number={4} checkStatus={setStatus}/>
                <Card number={2} checkStatus={setStatus}/>
                <Card number={1} checkStatus={setStatus}/>
            </div>
            <div className="row">                
                <Card number={5} checkStatus={setStatus}/>                                
                <Card number={2} checkStatus={setStatus}/>
                <Card number={3} checkStatus={setStatus}/>
                <Card number={6} checkStatus={setStatus}/>
            </div>
            <div className="row">                
                <Card number={4} checkStatus={setStatus}/>                                
                <Card number={6} checkStatus={setStatus}/>
                <Card number={5} checkStatus={setStatus}/>
                <Card number={3} checkStatus={setStatus}/>
            </div>
        </div>
        );
}

export default GameField;
