import React, { useState } from 'react';
import useGameState from '../CustomHooks/UseGameState'
import './GameField.css'
import Card from './Card';

function GameField() {
    const { 
        availables, 
        addToSelecteds, 
        removeFromSelecteds } = useGameState();

    const canHide = (number) => {
        if (availables.includes(number)) 
            return true;
        return false;
    }
      
    return (
        <div className="game-field">             
            <div className="row">                
                <Card number={1} onReveal={addToSelecteds} onHide={removeFromSelecteds} canHide={canHide} />                                
                <Card number={4} onReveal={addToSelecteds} onHide={removeFromSelecteds} canHide={canHide}/>
                <Card number={2} onReveal={addToSelecteds} onHide={removeFromSelecteds} canHide={canHide}/>
                <Card number={1} onReveal={addToSelecteds} onHide={removeFromSelecteds} canHide={canHide}/>
            </div>
            <div className="row">                
                <Card number={5} onReveal={addToSelecteds} onHide={removeFromSelecteds} canHide={canHide}/>                                
                <Card number={2} onReveal={addToSelecteds} onHide={removeFromSelecteds} canHide={canHide}/>
                <Card number={3} onReveal={addToSelecteds} onHide={removeFromSelecteds} canHide={canHide}/>
                <Card number={6} onReveal={addToSelecteds} onHide={removeFromSelecteds} canHide={canHide}/>
            </div>
            <div className="row">                
                <Card number={4} onReveal={addToSelecteds} onHide={removeFromSelecteds} canHide={canHide}/>                                
                <Card number={6} onReveal={addToSelecteds} onHide={removeFromSelecteds} canHide={canHide}/>
                <Card number={5} onReveal={addToSelecteds} onHide={removeFromSelecteds} canHide={canHide}/>
                <Card number={3} onReveal={addToSelecteds} onHide={removeFromSelecteds} canHide={canHide}/>
            </div>
        </div>
        );
}

export default GameField;
