import React,  { useState, useEffect } from 'react';
import { PropTypes } from 'prop-types';
import './Card.css'
import CardBack from '../images/back_card.jpg';

function Card(props) {    
    const [showImg, setShowImg] = useState(false);
    const [imageSource, setImageSource] = useState("");

    useEffect(() => {
        if (imageSource === "")
            setImageSource(props.imageUrl);
    })
       
    const revealNumber = () => {              
        setShowImg(props.onReveal(props.number));

        if (!props.OnCheckAvailables(props.number))
            setImageSource(props.grayImageUrl);
    }

    const hideNumber = () => {
        if (props.canHide(props.number))
        {
            props.onHide(props.number);
            setShowImg(false);
        }        
    }

    return (
        <div className="col-sm-3">           
            <input 
                type="image" 
                onClick={revealNumber} 
                className="img-card" 
                src={CardBack}
                style={{display: showImg ? 'none' : 'inline'}}
                >
            </input>       
            <input 
                type="image"
                onClick={hideNumber}          
                className="img-card"
                src={imageSource}
                style={{display: showImg ? 'inline' : 'none'}}     
                >
            </input>
        </div>
        );
}

Card.propTypes  = {
    imageUrl: PropTypes.string,
    grayImageUrl: PropTypes.string,
    number: PropTypes.number,    
    onReveal: PropTypes.func,
    onHide: PropTypes.func,
    canHide: PropTypes.func,
    OnCheckAvailables: PropTypes.func,
}

export default Card;
