import React, { useStatus } from 'react';
import Card from './Card';
import { PropTypes } from 'prop-types';

const utils = {
    range: (min, max) => Array.from({length: max - min + 1}, (_, i) => min + i)
}

const CardList = (props) => {
    const [availableNumbers, setAvailableNumbers] = useStatus([1, 2, 3, 4, 5, 6]);
    const [usedNumbers, setUsedNumbers] = useStatus([]);

    const getRandomNumbers = () => {
        let number = utils.range(1, 6);
        if (usedNumbers.includes(number))
            setAvailableNumbers(availableNumbers.filter(n => n !== number));
        else 
            setUsedNumbers(usedNumbers.concat(number));    

        return number;
    }
    

    return (
        <div className="game-field">             
            <div className="row">                
                <Card number={getRandomNumbers} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide} />                                
                <Card number={getRandomNumbers} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
            </div>
            <div className="row">                
                <Card number={getRandomNumbers} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>                                
                <Card number={getRandomNumbers} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
            </div>
            <div className="row">                
                <Card number={getRandomNumbers} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>                                
                <Card number={getRandomNumbers} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={getRandomNumbers} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
            </div>
        </div>
        );
}

CardList.propTypes  = {    
    addToSelecteds: PropTypes.func,
    removeFromSelecteds: PropTypes.func,
    canHide: PropTypes.func
}

export default CardList;
