import React, { useState } from 'react';
import Card from './Card';
import { PropTypes } from 'prop-types';
import { getNodeText } from '@testing-library/react';

const utils = {
    range: (min, max) => Array.from({length: max - min + 1}, (_, i) => min + i),

    random: (min, max) => min + Math.floor(Math.random() * (max - min + 1)),
}

const createRandomNumbers = () => {
    let availableCardsNumbers = utils.range(1, 6);    
    let usedCardNumbes = [];    
    let array2 = [];
    let index = 0;
    while (availableCardsNumbers.length > 0){
        let cardNumber = availableCardsNumbers[utils.random(1,6)];
        if (!usedCardNumbes.includes(cardNumber)){
            array2[index] = cardNumber;
            index++;
        }
            
    }
    return array2;
}

const CardList = (props) => {
    const [cardNumbers] = useState(createRandomNumbers());

    // const RenderCards = () => {
    //     var cards = [];
    //     for (let index = 0; index < cardNumbers.length; index++) {
    //         cards[index] = <Card key={index} number={cardNumbers[index]} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide} />         
    //     }
    //     return cards;
    // }
    

    return (

        <div className="game-field">             
            <div className="row">                
                <Card number={cardNumbers[0]} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide} />                                
                <Card number={cardNumbers[1]} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={cardNumbers[2]} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={cardNumbers[3]} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
            </div>
            <div className="row">                
                <Card number={cardNumbers[4]} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>                                
                <Card number={cardNumbers[5]} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={cardNumbers[6]} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={cardNumbers[7]} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
            </div>
            <div className="row">                
                <Card number={cardNumbers[8]} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>                                
                <Card number={cardNumbers[9]} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={cardNumbers[10]} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
                <Card number={cardNumbers[11]} onReveal={props.addToSelecteds} onHide={props.removeFromSelecteds} canHide={props.canHide}/>
            </div>
        </div>
        );
}

CardList.propTypes  = {    
    addToSelecteds: PropTypes.func,
    removeFromSelecteds: PropTypes.func,
    canHide: PropTypes.func
}

export default CardList;
