import React,  { useState, useRef, useEffect } from 'react';
import { PropTypes } from 'prop-types';
import './Card.css'
import CardBack from '../images/back_card.jpg';

function Card(props) {    
    const [showImg, setShowImg] = useState(false);    

    useEffect(() => {
        if (!props.availables.includes(props.number))
            imageRef.current.src = props.buildGrayImageUrl(props.number);
    },[props.availables])

    const revealNumber = () => {              
        setShowImg(props.onReveal(props.number));             
    }

    const hideNumber = () => {
        if (props.canHide(props.number))
        {
            props.onHide(props.number);
            setShowImg(false);
        }        
    }

    const imageRef = useRef(null);

    return (
        <div className="col-sm-3">           
            <input 
                type="image" 
                onClick={revealNumber} 
                className="img-card" 
                src={CardBack}
                style={{display: showImg ? 'none' : 'inline'}}
                >
            </input>       
            <input 
                type="image"
                onClick={hideNumber}          
                className="img-card"
                src={props.buildImageUrl(props.number)}
                ref={imageRef}
                style={{display: showImg ? 'inline' : 'none'}}     
                >
            </input>
        </div>
        );
}

Card.propTypes  = {
    buildImageUrl: PropTypes.func,
    buildGrayImageUrl: PropTypes.func,
    number: PropTypes.number,    
    onReveal: PropTypes.func,
    onHide: PropTypes.func,
    canHide: PropTypes.func,
    availables: PropTypes.array
}

export default Card;
